ivec2 mod(ivec2 x, ivec2 p) { return x-(x/p)*p; }
ivec3 mod(ivec3 x, ivec3 p) { return x-(x/p)*p; }
ivec4 mod(ivec4 x, ivec4 p) { return x-(x/p)*p; }


ivec2 convert_position_fast(ivec2 p1, ivec2 s1, ivec2 s2, ivec2 d2, int offset) {
