    ivec2 index_partial = p1 * s1;
    ivec2 index = ivec2(index_offset);
    ivec2 p2 = ivec2(0);

    ivec2 m;

            index += index_partial[0];
            m = index / s2;
            p2 += m;
            index -= m*s2;


            index += index_partial[1];
            m = index / s2;
            p2 += m;
            index -= m*s2;


    return p2-(p2/d2)*d2;
}

